import { useEffect, useState, useContext } from 'react'
import { useLocation } from 'react-router-dom';
import { caractersContext } from '../context/userContext';

 




function CharacterPlanet(){
    let location = useLocation();
    let {caracters} = useContext(caractersContext);
    let [charactersPlanet, setCharacters ] = useState([]);



    useEffect(()=> { 

        async function setData(){
            console.log("asd")
            if(location.state){
                console.log(location.state.location);
                console.log(caracters);
    
                let result = await caracters.filter(caracter => caracter.location.name === location.state.location);
                console.log(result)
        
                setCharacters(charactersPlanet => [...charactersPlanet, ...result])

                console.log(charactersPlanet)


    
            }

            
        }

        setData();
       
    }, [location, caracters]);


    

    return (
        
        <div className="d-flex flex-wrap">  
 

        </div>
    )
  
}


export default CharacterPlanet
