import { useEffect, useState, useContext, useCallback } from 'react'
import { useLocation } from 'react-router-dom';
import { caractersContext } from '../context/userContext';

 




function CharacterPlanet(){
    let location = useLocation();
    let {caracters} = useContext(caractersContext);
    let [charactersPlanet, setCharacters ] = useState([]);

    const fetchBusinesses = useCallback(() => {
        console.log("asd")
        if(location.state){
            console.log(location.state.location)
            let result = caracters.filter(caracter => caracter.location.name === location.state.location);
            console.log(result)
            setCharacters(result => [...charactersPlanet, ...result])
            console.log(charactersPlanet)

        }
    });

    useEffect(()=> {

      
        fetchBusinesses();
    }, [fetchBusinesses]);


    
 


  
}


export default CharacterPlanet
