import { useEffect, useState, useContext } from 'react'
import { useLocation } from 'react-router-dom';
import { caractersContext } from '../context/userContext';

 




function CharacterPlanet(){
    let location = useLocation();
    let {caracters} = useContext(caractersContext);



    useEffect(()=> { 

        async function setData(){
            console.log("asd")
            if(location.state){
                console.log(location.state.location);
                console.log(caracters);
    
                var result = await caracters.filter(caracter => caracter.origin.name === location.state.location);
                console.log(result)
        
                setCharacters(charactersPlanet => [charactersPlanet, ...result])

              

                console.log(charactersPlanet)
    
            }

            
        }

        setData();
       
    }, [location]);


    

    return (
        
        <div className="d-flex flex-wrap">  

            {
                caracters.map((character, index) => {
                    return (
                     
                            <div  key={index} className="containerplanets d-flex flex-wrap"> 
                               {location == character.origin.name && 
                                <div>character.origin.name</div>

                                }
                            
                            </div>
                        

                    )
                })
            }

        </div>
    )
  
}


export default CharacterPlanet
