import { useState, useEffect } from 'react'

function CharacterList(){

    const [characters, setCaracters ] = useState([]);
    useEffect(()=> {
        async function getData(){
            const response = await fetch('https://pokeapi.co/api/v2/ability/?limit=20&offset=20')
            const data = await response.json();
            setCaracters(data.results);

            console.log(data)
        }
        getData();
    }, []);

    return (
        <div>
            {
                characters.map(character => {
                    return (

                        
                        <div  key={character.id} class="card" style="width: 18rem;">
                            <img src={character.image} alt={character.name} class="card-img-top" />
                            <div class="card-body">
                                <h5 class="card-title">Card title</h5>
                                <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                                <a href="#" class="btn btn-primary">Go somewhere</a>
                            </div>
                        </div>
                    )
                })
            }
        </div>
    )
}




export default CharacterList
