import { useEffect, useState } from 'react'






function Login(){


    const [user, setUser] = useState([]) 

    useEffect(()=> {
        setTimeout(() => setUser({ email: '', password: '' }), 1000);
    }, []);


    function setValues(email, password){
        setUser({ email: email, password: password})
    }


    

   function  setValuesPassword(event) {
        console.log(e)

     
            setUser({password: event.target.value});
        
    };
   
    function handleChange(){
        console.log(user);
    }



    return(

        <form>
            <div className="form-group">
                <label className="text-muted">Email</label>
                <input   type="email" className="form-control" />
            </div>
            <div className="form-group">
                <label className="text-muted">Password</label>
                <input onChange={setValuesPassword(event)}   type="password" className="form-control" />
            </div>
            <button onClick={handleChange} className="btn btn-raised btn-primary">
                Entrar
            </button>
        </form>

    )
}



export default Login
