import { useState, useEffect } from 'react'
import Planet1 from '../files/p-1.png';
import Planet2 from '../files/p-2.png';
import Planet3 from '../files/p-3.png';
import Planet4 from '../files/p-4.png';
import Planet5 from '../files/p-5.png';

function CharacterList(){
    const [worlds, setCaracters ] = useState([]);

    let array = [];
    useEffect(()=> {
        async function getData(){
            for (let p = 1; p < 20; p++){

                

                const response = await fetch('https://rickandmortyapi.com/api/character/?page=' + p)
                const data = await response.json();
    
                for(let i = 0; i < 20; i++){
                    if(data.results[i].origin){
                        let searchLocation = array.findIndex(({location}) => location == data.results[i].origin.name)

                        if(searchLocation == -1){
                            array.push({location: data.results[i].origin.name, number: 1});
                        }

                        if(searchLocation != -1){
                            array[searchLocation].number = array[searchLocation].number + 1;
                        }
                    }                  
                }

                

            }   

            array.sort((a, b) => b.number - a.number);
            for(let e = 0; e < 8; e++){
                worlds[e] = array[e]
            }
        
          
        }

        getData();
        console.log(worlds);
    
    }, []);

    return (
        <div className="d-flex flex-wrap">
             <img className="universe" src={'https://sitiorandom.com/wp-content/uploads/2019/05/Fondo.png'}/>
            {
                worlds.map((world, index) => {
                    return (
                        <div  key={index} className="containerplanets d-flex flex-wrap">
                           {index ? <img className="imageplanet" src={Planet1} /> : 1}
                           {index ? <img className="imageplanet" src={Planet2} /> : 2}
                           {index ? <img className="imageplanet" src={Planet3} /> : 3}
                           <div>{index}</div>
                        </div>

                        
                    )
                })
            }
        </div>
    )
}




export default CharacterList
