import { useState, createContext, useContext} from 'react'

const context = createContext();
const UserData = () => {
    useContext(context);
}


export const UserContainer = ({children}) => {
    const [user, setUser] = useState([]);

    console.log(user);

    return (
        <context.Provider value={{
            user,
            setUser
        }}>
            {children}
        </context.Provider>
    )
}
