import { useState, useContext } from 'react'

export const context = createContext();


export const userContainer = ({children}) => {
    const [user, setUser] = useState([]);

    console.log(user);

    return (
        <context.Provider value={{
            user,
            setUser
        }}>
            {children}
        </context.Provider>
    )
}
