import { useEffect, useState, useContext } from 'react'
import { useLocation } from 'react-router-dom';
import { caractersContext } from '../context/userContext';

 




function CharacterPlanet(){
    let location = useLocation();
    const {caracters} = useContext(caractersContext);
    const [charactersPlanet, setCharacters ] = useState([]);

    useEffect(()=> {
        console.log("asd")
        if(location.state){
            console.log(location.state.location)
            const result = caracters.filter(caracter => caracter.location.name == location.state.location);
            setCharacters(result)
            console.log(charactersPlanet)
            
        }
    }, [location]);


    
 


  
}


export default CharacterPlanet
