            array.sort((a, b) => b.number - a.number);
            for(let e = 0; e < 8; e++){
                worlds[e] = array[e]
            }
        
          console.log(worlds);
        }
        getData();
    
    }, []);

    return (
        <div className="d-flex flex-wrap">
             <img className="universe" src={'https://sitiorandom.com/wp-content/uploads/2019/05/Fondo.png'}/>
            {
                worlds.map(world => {
                    return (
                        <div  key={world.id} className="containerplanets d-flex flex-wrap">
                            <img className="imageplanet" src={Planet1} />
                        </div>

                        
                    )
                })
            }
        </div>
    )
}




export default CharacterList
