import { useState, useEffect } from 'react'

function CharacterList(){

    const [characters, setCaracters ] = useState([]);
    useEffect(()=> {
        async function getData(){
            let array = [];


            for (let p = 1; p < 40; p++){

                const response = await fetch('https://rickandmortyapi.com/api/character/?page=' + p)
                const data = await response.json();
    
                for(let i = 0; i < 20; i++){

console.log(data.results[i].origin)
                    if(data.results[i].origin){
                        let searchLocation = array.findIndex(({location}) => location == data.results[i].origin)

                    if(searchLocation == -1){
                        array.push({location: data.results[i].origin.name});
                    }

                    }
                    
                 
                }

            }
  
          
                   
          console.log(array)

            

        }
        getData();
    }, []);

    return (
        <div className="d-flex flex-wrap justify-content-center">
            {
                characters.map(character => {
                    return (
       
                        <div  key={character.id} className="card m-3  " >
                            <img src={character.image} alt={character.name} className="card-img-top" />
                            <div className="card-body">
                                <h5 className="card-title">{character.name}</h5>
                                <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                                <p>{character.origin.name}</p>
                            </div>
                        </div>
                    )
                })
            }
        </div>
    )
}




export default CharacterList
