import { useEffect, useState } from 'react'






function Login(){


    const [user, setUser] = useState([]) 

    useEffect(()=> {
        setTimeout(() => setUser({ email: '', password: '' }), 1000);
    }, []);


   


    setValues = (name) => (event) => {
        console.log(event)

        
        if(event._wrapperState._wrapperState == "email"){
            this.setState({[email]: event.target.value});
        }
    };
   
    function handleChange(){
        console.log(user);
    }


    


    return(

        <form>
            <div className="form-group">
                <label className="text-muted">Email</label>
                <input onChange={setValues()}  value={user.email} type="email" className="form-control" />
            </div>
            <div className="form-group">
                <label className="text-muted">Password</label>
                <input onChange={setValues()}  value={user.password} type="password" className="form-control" />
            </div>
            <button onClick={handleChange} className="btn btn-raised btn-primary">
                Entrar
            </button>
        </form>

    )
}



export default Login
