import { useState, useEffect } from 'react'

function CharacterList(){
    const [array, setCaracters ] = useState([]);
    useEffect(()=> {
        async function getData(){
            let array = [];
            for (let p = 1; p < 40; p++){

                const response = await fetch('https://rickandmortyapi.com/api/character/?page=' + p)
                const data = await response.json();
    
                for(let i = 0; i < 20; i++){
                    if(data.results[i].origin){
                        let searchLocation = array.findIndex(({location}) => location == data.results[i].origin.name)

                        if(searchLocation == -1){
                            array.push({location: data.results[i].origin.name, number: 1});
                        }

                        if(searchLocation != -1){
                            array[searchLocation].number = array[searchLocation].number + 1;
                        }
                    }                  
                }

            }   
          array.sort((a, b) => b.number - a.number);
          console.log(array);
        }
        getData();
    
    }, []);

    return (
        <div className="d-flex flex-wrap justify-content-center">
            {
                array.map(world => {
                    return (
                        <div  key={world.id} className="card">
                        sadasdasf as afssa sa as  safs afsa sas sa sa fsa  sasas af 
                        </div>
                    )
                })
            }
        </div>
    )
}




export default CharacterList
