import { useState, useEffect } from 'react'

function CharacterList(){

    const [characters, setCaracters ] = useState([]);
    useEffect(()=> {
        async function getData(){
            let array = [];
            for(let n = 1; n == 42; n++){
                const response = await fetch('https://rickandmortyapi.com/api/character/?page=' + n)

                for(let i = 0; response.results.lenght; i++){
                    array.push(response.result[i].location.name);


                }
            }

            console.log(array)
            
            const data = await response.json();
            setCaracters(data.results);

        }
        getData();
    }, []);

    return (
        <div className="d-flex flex-wrap justify-content-center">
            {
                characters.map(character => {
                    return (
       
                        <div  key={character.id} className="card m-3  " >
                            <img src={character.image} alt={character.name} className="card-img-top" />
                            <div className="card-body">
                                <h5 className="card-title">{character.name}</h5>
                                <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                                <p>{character.origin.name}</p>
                            </div>
                        </div>
                    )
                })
            }
        </div>
    )
}




export default CharacterList
