import { useState, createContext} from 'react';

export const userContext = createContext();



export const CaractersProvider = ({children}) => {
    const [user, setCaracters] = useState([])



    return (
        <userContext.Provider value={{
            user,
            setCaracters
            
        }}>
            {children}
        </userContext.Provider>
    )
}
